package ForLoop;

public class forLoopExample2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number = 2;
		//loop for table of 2
	     for (int i = 1; i < 10; i++) {
             
	    	 //multiplying i with number(2) by running loop
	    	 System.out.println( number * i);
	     }
	           
	}

}

