package breakTheLoop;

public class breakStatementexample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int  n = 1;
	       
	       while (n<=10)
	       {
	    	   if(n==6)
	    	   {
	    		   break;
	    	   }
	    	   System.out.println(n);
	    	   n++;
		    
	       }
	}

}
