package doWhile;

public class doWhileExamples5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int countdown = 10;
	        
	        do {
	            System.out.println(countdown);
	            countdown--;
	        } while (countdown > 0);
	        
	        System.out.println("Time's up!");
	    }
	}


