package whileLoop;

public class whileLoop5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          int num = 51;  // Start from 51
        
        while (num % 2 != 0) {  // Continue while num is odd
            num++;
        }
        
        System.out.println("The first even number greater than 50 is: " + num);
    }
	}


